      w.prepend('A');
      w.prepend('B');
      w.print();
      w.del();
      w.print();
      p = &w;
      p -> print();
      cout << "exiting inner block" << endl;
   }
   //p -> print();  gives system dependent behavior
   cout << "exiting outer block" << endl;
}

