matrix::matrix(int d1, int d2) : s1(d1), s2(d2)
{
   assert (d1 > 0 && d2 > 0);
   p = new int*[s1];
   assert(p != 0);
   for (int i = 0; i < s1; ++i)
      p[i] = new int[s2];
}

