{
   for (int i = 0; i <= ub1(); ++i)
      delete p[i];
   delete []p;
}

int& matrix::element(int i, int j)
{
   assert(i >= 0 || i <= ub1() || j >= 0 || j <= ub2());
   return p[i][j];
}


vect mpy(const vect& v, const matrix& m)
{
   assert(v.size == m.s1);    //check sizes
   //use privileged access to p in both classes
   vect  ans(m.s2);
   int   i, j;

   for (i = 0; i <= m.ub2(); ++i) {
      ans.p[i] = 0;
      for (j = 0; j <= m.ub1(); ++j)
         ans.p[i] += v.p[j] * m.p[j][i];
   }
   return ans;
}

